        recyclerView.addOnItemTouchListener(
                new RecyclerItemClickListener(WazaifListActivity.this, recyclerView ,new RecyclerItemClickListener.OnItemClickListener() {
                    @Override public void onItemClick(View view, int position) {
                        // do whatever
                        Models model = list.get(position);
                        String title = model.getWazaifTitle().toString();
                        String Details = model.getWazaifDetails().toString();
                        String Triqa = model.getTriqaAmal().toString();
                        Intent intent = new Intent(WazaifListActivity.this, FullWazaifActivity.class);
                        intent.putExtra("wazaifTitle", title);
                        intent.putExtra("wazaifDetails", Details);
                        intent.putExtra("wazaifTriqa", Triqa);
                        startActivity(intent);
                    }

                    @Override public void onLongItemClick(View view, int position) {
                        // do whatever
                    }
                })
        );


